    public static List createWorkers(int numberOfWorkers) {

        ArrayList<Worker> workers = new ArrayList<>();

        for (int i = 0; i < numberOfWorkers; i++) {
            workers.add(randomWorker());

        }

        return workers;
    }

    public static List createRoster(int rosterSize, int rosterLevel) {

        ArrayList<Worker> roster = new ArrayList<>();

        for (int i = 0; i < rosterSize; i++) {
            roster.add(randomWorker(rosterLevel));
        }
        return roster;
    }

    //to generate a random worker at a given popularity level
    public static Worker randomWorker(int level) {

        if (level < 1) {
            level = 1;
        } else if (level > 5) {
            level = 5;
        }

        Worker worker = randomWorker();

        //set the popularity to be proportionate to the level requested
        worker.setPopularity((level * 20) - 10 + RandomUtils.nextInt(0, 10));

        //prevent too many maxed out workers
        if (worker.getPopularity() > 100) {
            worker.setPopularity(90 + RandomUtils.nextInt(0, 10));
        }

        return worker;
    }

    public static Worker randomWorker() {
        Worker worker = new Worker();

        worker.setPopularity(RandomUtils.nextInt(0, 100));
        worker.setFlying(RandomUtils.nextInt(0, 100));
        worker.setCharisma(RandomUtils.nextInt(0, 100));
        worker.setBehaviour(RandomUtils.nextInt(0, 100));
        worker.setWrestling(RandomUtils.nextInt(0, 100));
        worker.setStriking(RandomUtils.nextInt(0, 100));
        worker.setAge(RandomUtils.nextInt(18, 60));
        worker.setGender(RandomUtils.nextInt(0, 2) == 1
                ? Gender.MALE : Gender.FEMALE);

        worker.setMainRoster(true);
        worker.setFullTime(true);

        setRandomName(worker);

        return worker;
    }

    public static StaffMember randomStaff(int level, StaffType staffType) {
        StaffMember staff = new StaffMember();

        staff.setStaffType(staffType);
        staff.setBehaviour(RandomUtils.nextInt(0, 100));
        staff.setAge(RandomUtils.nextInt(18, 60));
        staff.setGender(RandomUtils.nextInt(0, 2) == 1
                ? Gender.MALE : Gender.FEMALE);
        staff.setSkill((level * 20) - 10 + RandomUtils.nextInt(0, 10));
        setRandomName(staff);

        return staff;
    }

    private static void setRandomName(iPerson person) {
        Random random = new Random();
        String nameString;

        List<String> names = person.getGender().equals(Gender.MALE)
                ? FIRST_NAMES_MALE : FIRST_NAMES_FEMALE;

        int index = random.nextInt(names.size());
        String firstName = names.get(index).trim();
        firstName = firstName.toLowerCase();
        firstName = firstName.substring(0, 1).toUpperCase() + firstName.substring(1);

        index = random.nextInt(LAST_NAMES.size());
        String lastName = LAST_NAMES.get(index);

        if (RandomUtils.nextInt(0, 25) == 0) {
            String nickName = NICK_NAMES.get(RandomUtils.nextInt(0, NICK_NAMES.size())).trim();
            nickName = StringUtils.capitalize(nickName);
            lastName = String.format("\"%s\" %s", nickName, lastName);
        }

        nameString = firstName + " " + lastName;

        person.setName(nameString);
        person.setShortName(lastName);
    }
